GRANT ALL PRIVILEGES ON DATABASE postgres to postgres;


CREATE TABLE public.user(id serial PRIMARY KEY, username varchar(25), password  varchar(225));

CREATE TABLE public.exceptionmessage(exid serial PRIMARY KEY, exception_id int, message  varchar(225));

CREATE TABLE public.planner(planner_id serial PRIMARY KEY, id  varchar(225), name  varchar(225), email  varchar(225));

CREATE TABLE public.materialmaster(id serial PRIMARY KEY, initial_creation_date varchar(225), last_change_date varchar(225),material varchar(225), material_9 varchar(225),	material_7 varchar(225),ai varchar(225), mat_description varchar(225),	mat_description_eng varchar(225),	plant varchar(225),	planner varchar(225),	replenishment_type varchar(225),	rounding_value varchar(225), lot_size varchar(225),	minimum_lot_size varchar(225),safety_time_days varchar(225),	safety_time_ind varchar(225),	leadtime_hours varchar(225),planning_time_fence varchar(225),planned_delivery_time varchar(225),	safety_stock varchar(225),	unit_of_quantity varchar(225),length varchar(225),	width varchar(225),	height varchar(225), uom_lwh varchar(225),	volume varchar(225), unit_of_volume varchar(225),	gross_weight varchar(225),	net_weight varchar(225), unit_of_weight varchar(225),stacking_factor varchar(225),	material_group varchar(225), run_out_date varchar(225),proc_type varchar(225),	part_type varchar(225),	character varchar(225),	init_upg varchar(225),init_use_type varchar(225),	procurement_type varchar(225),	storage_location varchar(225),	bwd_consumption_per varchar(225),	fwd_consumption_per varchar(225),	pps_planning_calendar varchar(225),	factory_calendar varchar(225),	mrp_group varchar(225),	mixed_mrp varchar(225),	strategy_group varchar(225),	consumption_mode varchar(225),abc_indicator varchar(225),spab_indicator varchar(225),	status varchar(225),status_valid_from varchar(225),	bulk_material varchar(225),	omb_part varchar(225),warehouse_number varchar(225),storage_type_stock_placement varchar(225), storage_type_stock_removement varchar(225),	release varchar(225),	release_date varchar(225),release_type varchar(225),	release_action_code varchar(225),	active_material varchar(225),	plant_calendar varchar(225),document_number varchar(225),	sid_client varchar(225),snapdate varchar(225),snaptimestamp varchar(225),	load_date varchar(225),	load_timestamp varchar(225), _load_date varchar(225));

CREATE TABLE public.exception(id serial PRIMARY KEY, mandt varchar(225), matnr varchar(225), aline varchar(225), cdate varchar(225), ctime varchar(225), dat00 varchar(225), delb0 varchar(225), extra varchar(225), umdat varchar(225), auskt int, mng01 varchar(225), mng02 varchar(225), p_ingestday varchar(225), p_ingesttime varchar(225));
    
CREATE TABLE public.md04(id serial PRIMARY KEY, material varchar(225), plant varchar(225), mrp_area varchar(225), demand_date varchar(225), mrp_element_cd varchar(225), shipping_notification varchar(225), container_info varchar(225), mrp_element varchar(225), change_quantity int, total_quantity int, storage_location varchar(225), supplier_nr varchar(225), planner varchar(225), split_indicator varchar(225), line_index varchar(225), document_uuid varchar(225), sid_client varchar(225), snapdate varchar(225), snaptime varchar(225), load_date varchar(225), load_timestamp varchar(225), _load_date varchar(225));

CREATE TABLE public.zgrve(id serial PRIMARY KEY, mandt varchar(225), lifnr varchar(225), delnote varchar(225), trailer varchar(225), matnr varchar(225), mblnr varchar(225), itmno varchar(225), erdat varchar(225), erfmg varchar(225), meins varchar(225), ebeln varchar(225), ebelp varchar(225), werks varchar(225), lgort varchar(225), bwart varchar(225), rct_ident varchar(225), crttime varchar(225), status varchar(225), shkzg varchar(225), vbeln varchar(225), bolnr varchar(225), p_ingestday varchar(225), p_ingesttime varchar(225));
